import React, { useEffect, useState } from "react";
import axios from "./axios";
import requests from "./requests";
import Keys from "./Keys";
import "./banner.css";

function Banner() {
  const [movie, setMovie] = useState([]);
  // console.log(requests.NetflixOriginals);
  useEffect(() => {
    async function fetchData() {
      const response = await axios.get(requests.NetflixOriginals);
      //getting a single random movie
      setMovie(
        response.data.results[
          Math.floor(Math.random() * response.data.results.length - 1)
        ]
      );
      return response;
    }
    fetchData();
  }, []);
  // console.log(movie);
  function truncate(str, lengthAll) {
    if (!str) return;
    return str.length > lengthAll ? str.substr(0, lengthAll - 1) + "..." : str;
  }


  return (
    <header
      className="banner"
      style={{
        backgroundImage: `url("https://image.tmdb.org/t/p/original/${movie.backdrop_path}")`,
      }}
    >
      <div className="banner__contents">
        <h1>{movie.title || movie.name || movie.original_name}</h1>

    

        
      </div>

      <div className="banner--fadeBottom"></div>
    </header>
  );
}

export default Banner;
